create table if not exists clients (id uuid default gen_random_uuid() primary key, created_at timestamptz default now(), name text unique); create table if not exists leads (id bigserial primary key, client_id uuid, name text, email text, role text, website text, pain_point text, status text default 'new'); create table if not exists campaigns (id bigserial primary key, client_id uuid, name text, subject text, status text default 'active'); create table if not exists outbox (id bigserial primary key, client_id uuid, to_email text, subject text, body_html text, status text default 'queued');